import React, { useEffect, useState } from 'react';
import { getAllCharacters } from '../Api/Api';
import CharactersList from '../CharactersList/CharactersList';
import { SortCharacters } from '../SortCharacters/SortCharacters';
import FilterCharacters from '../FilterCharacters/FilterCharacters';




const Home = () => {
    const [allCharacters, setAllCharacters] = useState([]);
    const [characters, setCharacters] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const heroes = await getAllCharacters();
                setAllCharacters(heroes);
                setCharacters(heroes); // Установите characters при первоначальной загрузке данных
            } catch (error) {
                console.error(error);
            }
        };

        fetchData();
    }, []);

    const updateCharacters = (sortedCharacters) => {
        setCharacters(sortedCharacters);
    };

    return (
        <div>
            <FilterCharacters/>
            <SortCharacters update={updateCharacters} data={characters} />
            <CharactersList characters={characters} />
        </div>
    );
};
 export default Home;
