import React, { useEffect, useState } from 'react';
import { getAllCharacters } from '../Api/Api';
import CharactersList from '../CharactersList/CharactersList';
import { SortCharacters } from '../SortCharacters/SortCharacters';
import axios from 'axios';



const Home = () => {
  const [allCharacters, setCharacters] = useState([]);


     useEffect(() => {
    axios.get(getAllCharacters).then(response => {
                setCharacters(response.data);
            })
            .catch(error => {
                console.error('Error loading data:', error);
            });
    }, []);

    const updateCharacters = (sortedCharacters) => {
        setCharacters(sortedCharacters);
    };


    return (
        <div>
            <SortCharacters />
            <CharactersList characters={allCharacters } />
        </div>
    )

}

 export default Home;
